import{u as a,o,c as s,a as e}from"./index.da078b25.js";const n={class:"markdown-body"},c=e("h1",null,"LoLChat",-1),r=e("p",null,"Back before the game League of Legends had built-in voice chat, I built this disposable voice chat service with a friend to allow players to connect up before matches. The idea was for a player to generate a channel, copy the channel link, and paste it into the chat for other players to join. It used WebRTC with STUN and TURN servers for Interactive Connectivity Establishment (ICE) in order to connect peers together, and Web Audio to display an indicator for which person is speaking.",-1),i=[c,r],T="LoLChat",f="A peer-to-peer disposable voice chat service.",u="2013-07-15T00:00:00.000Z",g="2014-11-12T00:00:00.000Z",b="2013-08-22T00:00:00.000Z",v="2015-09-01T00:00:00.000Z",E=!1,L=["FRONTEND","WEBSITE"],Z=[],y={__name:"index",setup(d,{expose:t}){return t({frontmatter:{name:"LoLChat",short_description:"A peer-to-peer disposable voice chat service.",start_date:"2013-07-15T00:00:00.000Z",end_date:"2014-11-12T00:00:00.000Z",release_date:"2013-08-22T00:00:00.000Z",close_date:"2015-09-01T00:00:00.000Z",highlight:!1,categories:["FRONTEND","WEBSITE"],meta:[]}}),a({meta:[]}),(p,_)=>(o(),s("div",n,i))}};export{L as categories,v as close_date,y as default,g as end_date,E as highlight,Z as meta,T as name,b as release_date,f as short_description,u as start_date};
